import type { Node } from 'unist';
import type { NodeWithEstree } from '../../types/mdx/index.js';
/**
 * An export looks like this in AST form:
 * {
 *   type: 'mdxjsEsm',
 *   data: {
 *     estree: {
 *       type: 'Program',
 *       sourceType: 'module'
 *       body: [
 *         type: 'ExportNamedDeclaration',
 *         ...
 *       ]
 *     }
 *   }
 * }
 * @param content mdx ast node
 * @returns whether the node includes an export or not
 */
export declare const nodeIncludesExport: (node: Node) => node is NodeWithEstree;
