import { getOpenApiOperationMethodAndEndpoint } from './getOpenApiOperationMethodAndEndpoint.js';
export const getOpenApiTitleAndDescription = (openApiFiles, openApiMetaField) => {
    if (openApiMetaField === '') {
        return {};
    }
    const openApiData = getOpenApiOperationMethodAndEndpoint(openApiMetaField, openApiFiles);
    if (openApiData == undefined) {
        return {};
    }
    return {
        title: openApiData.operation.summary,
        description: openApiData.operation.description,
    };
};
